import{T as e}from"./TopNav-D4Fz84cq.js";import{_ as a,c as r,a as t,b as s,o as n}from"./index-CE0Nud0F.js";const d="/assets/hero-img-CemFhB33.png",o={class:"contentContainer"},i=s('<img src="'+d+'" alt="Om haven" class="topBannerImage" data-v-c2942e59><div class="aboutText" data-v-c2942e59><h1 class="titleHeader" data-v-c2942e59>Om haven</h1><div data-v-c2942e59><h2 class="gardenOpenTitle" data-v-c2942e59>Åbningstider</h2><p data-v-c2942e59>Alle hverdage 10-19 <br data-v-c2942e59> Weekendt og helligedage 10-17</p></div><div data-v-c2942e59><h2 class="prices" data-v-c2942e59>Priser</h2><p data-v-c2942e59>Geografisk Have er en 14 hektar stor, botanisk have, hvor det geografiske relaterer sig til, at træer, buske og urter er plantet sammen efter oprindelsesland.</p></div><div data-v-c2942e59><h2 class="botanicGarden" data-v-c2942e59>Botanisk Have</h2><p data-v-c2942e59>Geografisk Have er en 14 hektar stor, botanisk have, hvor det geografiske relaterer sig til, at træer, buske og urter er plantet sammen efter oprindelsesland. <br data-v-c2942e59> Grundlæggeren Aksel Olsen Købte jorden og startede stedet som planteskole i 1918. Han havde en stor interesse for planter fra hele verden og især fra Østasien. Han testede om 6000 planter kunne klare sig i vores danske klima og i dag er ca. 2000 af dem tilbage i haven.</p></div><div data-v-c2942e59><h2 class="inspiringGarden" data-v-c2942e59>Inspirerende have</h2><p data-v-c2942e59>lad dig inspirere af Naturhaven, Krydderurtehaven, Den Økologiske Køkkenhave, Staudehaven og mange andre dejlige områder. Den prisbelønnede Rosenhave er især en fantastisk duftoplevelse om sommeren med over 5000 moderne roser. I Rosariet kan du gå på opdagelse i både historiske og vilde roser. I Syvdalen venter spændende og mere vilde natureventyr med grotter, bæk og kvidrende fuglesang. Følger du den 1,8 km lange Hjertesti med fuglesang, vil du også opleve det rige fugleliv, som er med til at give stedet en helt særlig stemning.</p></div><div data-v-c2942e59><h2 class="bringKids" data-v-c2942e59>Tag børnene med</h2><p data-v-c2942e59>Haven er et mangfoldigt sted, med plads til gæster i alle aldre. Mange bruger Geografisk Have som mødested eller som et grønt åndehul. For børn og unge er det særligt populært at besøge Havens legepladser, Bambuslunden, Grotterne og Dyrefolden med klappegeder, kaniner, høns, klippeegern, ponyer og Havens kat.</p></div></div>',2),l={__name:"AboutGarden",setup(v){return(g,c)=>(n(),r("div",o,[t(e),i]))}},h=a(l,[["__scopeId","data-v-c2942e59"]]);export{h as default};
